from unidiff import PatchSet


def get_structured_diff_from_diff_string(diff_text: str):
    patch = PatchSet(diff_text)
    return patch


def get_changed_files(diff_text: str) -> list:
    """
    Parse the diff text and return a list of file paths that were changed.

    :param diff_text: The unified diff text.
    :return: A list of file paths.
    """
    patch = PatchSet(diff_text)
    return [patched_file.path for patched_file in patch]

